import { produce } from 'immer';

const USER_PHONE = 'userinfo/USER_PHONE';
export const GLOBAL_LOADING = "LOADING";
export const GLOBAL_LOADED = "LOADED";

const API_KEYS = 'userinfo/APK_KEYS';

export const userPhone = (  cell: string ) => ({
    type : USER_PHONE,
    cell
});

export const apiKeys = ( keys : any ) => ({
    type : API_KEYS,
    keys
})

type PhoneType = {
    cell : string
    loading : boolean
    keys : any
}

const initialState : PhoneType = {
    cell : '',
    loading : false,
    keys : ''
};

function userInfo(state = initialState, action: { type: any; cell : string, loading : boolean, keys : any })  {
    switch (action.type){
        case  USER_PHONE :
            return produce(state, draft => {
                draft.cell = action.cell
            });
        case GLOBAL_LOADING :
            return produce(state, draft => {
                draft.loading = true
            });
        case GLOBAL_LOADED :
            return produce(state, draft => {
                draft.loading = false
            });
        case API_KEYS :
            return produce(state, draft => {
                draft.keys = action.keys
                draft.keys = action.keys
            });
        default : return state;
    }
}
export default userInfo;
