import React, {useEffect, useState} from "react";
import { sendMessage } from "../../pages/api/sendMessage";
import { randomCode } from "../util/common";
import {axios} from "../../pages/api/axios";
type sendParam = {
    code : string,
    cell : string
}
const SendButton = () => {
    const [sendFlag, setSendFlag] = useState<boolean>(false);
    const [ param, setParam ] = useState<sendParam>();
    const onClickHandler = (e : React.MouseEvent<HTMLElement>) => {
        setParam(
            {
                cell : '01092673178',
                code : randomCode()
            }
        );
    }
    useEffect(() => {
       let res = axios.post('',param)
           .then((res) => {
               console.log(res);
           }).catch((err) => {
               console.log(err);
           })
    }, [param]);
    return (
        <div>
            <button className='button_off' onClick={onClickHandler} disabled={sendFlag}>인증번호 전송</button>
        </div>
    )
}
export default SendButton;
