import React, {useEffect, useState} from "react";

type InfoProps = {
    bpk : string,
    cName : string,
    cCell : string,
    cJumin : string,
    cMail : string,
    cPost : string,
    cAddr1 : string,
    cAddr2 : string,
    cJob : string,
    carNm : string,
    carUse : string,
}
const JuminInput = (props : {info : InfoProps, setInfo : React.Dispatch<React.SetStateAction<InfoProps>>}  ) => {
    const [birth , setBirth] = useState<string>('');
    const [jumin, setJumin] = useState<string>('')
    const [check, setCheck] = useState<boolean>(false);
    const juminRegex = /\d{2}([0]\d|[1][0-2])([0][1-9]|[1-2]\d|[3][0-1])[-]*[1-4]\d{6}/g;
    const onChangeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
    }
    useEffect(() => {
        if((birth + jumin).length === 13) {
            let juminAll = birth + jumin;
            console.log(juminAll);
            if (juminRegex.test(juminAll)) {
                setCheck(true);
                props.setInfo((prevState) => {
                    return { ...prevState, cJumin: jumin }
                });
            } else {
                setCheck(false);
            }
        }
    }, [birth,jumin]);
    return(
        <div className='flex flex-col pb-8'>
            <div className='sub_title pb-1 pl-2'>
                주민번호
            </div>
            <div className='flex justify-between mx-2'>
                <input type={'text'} onChange={(e : React.ChangeEvent<HTMLInputElement>) => setBirth(e.target.value)} className='input basis-1/3' placeholder='앞자리'/>
                <input type={'text'} onChange={(e : React.ChangeEvent<HTMLInputElement>) => setJumin(e.target.value)} className='input input basis-1/3' placeholder='뒷자리'/>
            </div>
            {!check && jumin !== '' && <h2 className='text-red-500 text-[11px] pt-2'>주민번호를 확인해주세요.</h2>}
        </div>
    )
}
export default JuminInput;
