import React, {useState} from "react";
import CloseIcon from "../../../assets/images/btn_close_24px.svg";
interface SearchProps{
    sel : boolean,
    setSel : React.Dispatch<React.SetStateAction<boolean>>
}
const SearchText = ({sel, setSel} : SearchProps) => {
    const [showSearch, setShowSearch] = useState(sel);
    const onClickHandler = (e : React.MouseEvent<HTMLElement>) => {
        setShowSearch(!showSearch);
        setTimeout(() => {
            setSel(!sel);
        }, 500);
    }
    return(
        <div>
            <div className='fixed inset-0 w-full z-10 '>
                <div className={showSearch ? 'absolute bg-white bottom-0 left-0 w-screen h-full bg-white animate-[left-side-on_600ms_ease-in-out]' : 'absolute bottom-0 h-full left-0  w-screen bg-white animate-[left-side-out_600ms_ease-in-out]'}>
                    <div className='flex items-center mt-4 mb-8 text-[14px] h-[45px] bg-[#f6fbf7] border-b-[2px] border-b-[#157b4b] title_text'>
                        <div className="flex w-full justify-center">개인(신용)정보의 조회에 관한 사항</div>
                        <div className='mr-2'> <button onClick={(e) => onClickHandler(e)}><img src={CloseIcon}/></button></div>
                    </div>
                    <div className='text-[11px] mx-8 flex justify-center'>
                        <div>
                            당사(DB손해보험) 및 당사 업무수탁자는 「개인정보보호법」 및 「신용정보의 이용 및 보호에 관한 법률」에 따라 귀하의 개인(신용)정보를 다음과 같이 조회하고자 합니다
                        </div>
                        <div>
                            <h2></h2>
                            <h2></h2>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}
export default SearchText;
