import DaumPostcode from 'react-daum-postcode';
import React, {useState} from "react";
const PostCode = () => {
    const [flag, setFlag] = useState<boolean>(false);
    const [address, setAddress] = useState<string>('');
    const [postCode, setPostCode] = useState<string>('');
    const handleComplete = (data : any) => {
        let fullAddress :string = data.address;
        let extraAddress = '';
        console.log(data)
        if (data.addressType === 'R') {
            if (data.bname !== '') {
                extraAddress += data.bname;
            }
            if (data.buildingName !== '') {
                extraAddress += (extraAddress !== '' ? `, ${data.buildingName}` : data.buildingName);
            }
            fullAddress += (extraAddress !== '' ? ` (${extraAddress})` : '');
            setAddress(fullAddress);
            setPostCode(data.zonecode);
        }
        setFlag(false)
    }
    return (
        <div className='flex flex-col w-[500px]'>
            <div className='flex justify-between'>
                <div className='border basis-1/4 flex justify-center'>{postCode}</div>
                <button onClick={(e:React.MouseEvent<HTMLElement>)=>setFlag(!flag)} className='basis-1/4 border py-2 rounde0d'>우편번호 검색</button>
            </div>
            { flag &&
                <div>
                    <DaumPostcode onComplete={handleComplete}/>
                </div>
            }
        </div>
    )
}
export default PostCode;
