import React, {useEffect, useState} from "react";

type InfoProps = {
    bpk : string,
    cName : string,
    cCell : string,
    cJumin : string,
    cMail : string,
    cPost : string,
    cAddr1 : string,
    cAddr2 : string,
    cJob : string,
    carNm : string,
    carUse : string,
}
const CarNumInput = (props : {info : InfoProps, setInfo : React.Dispatch<React.SetStateAction<InfoProps>>}) => {
    const [carNum, setCarNum] = useState<string>('');
    useEffect(() => {
        props.setInfo((prevState) => {
            return { ...prevState, carNm: carNum }
        });
    }, [carNum]);
    return (
        <div className='flex flex-col pb-8 px-2'>
            <div className='sub_title pb-1 pl-2'>
                차량번호
            </div>
            <input type={'text'} onChange={(e:React.ChangeEvent<HTMLInputElement>) => setCarNum(e.target.value)} className='input' placeholder='차량번호를 입력해주세요'/>
        </div>
    )
}
export default CarNumInput;
