import {useEffect, useState} from "react";

interface Props{
    code : string,
    inputCode : string
}
const ConfirmButton = ({code, inputCode} : Props) => {
    const [isFlag, setFlag] = useState<boolean>(false);
    const onClickHandler = (e : React.MouseEvent<HTMLElement>) => {
        if(inputCode !== ''){
            setFlag(false);
        }else {
            setFlag(true);
        }
        if(code === inputCode){
            alert('통과');
        }else{
            alert('잘못된 인증번호 입니다.');
        }
    }
    useEffect(() => {

    }, [inputCode]);
    return (
        <div>
            <button className={isFlag ? 'button_off' : 'button_on'} onClick={onClickHandler} disabled={isFlag}>확인</button>
        </div>
    )
}
export default ConfirmButton;
