import React, {useState, useEffect, useRef} from 'react';
import cn from 'classnames';
interface Props {
    exampleRef : any,
    infoRef : any,
    limitRef : any,
    qaRef : any
}

export default function Header({exampleRef, infoRef, limitRef, qaRef} : Props) {
    // 스크롤 여부를 감지하기 위한 state
    const [scrolled, setScrolled] = useState(false);
    // 각 섹션에 대한 ref 생성

    // 스크롤 이벤트 핸들러
    const handleScroll = () => {
        const isScrolled = window.scrollY > 0; // 스크롤이 발생했는지 여부를 확인
        setScrolled(isScrolled); // 스크롤 여부를 state에 반영
    };

    // 컴포넌트가 마운트될 때 스크롤 이벤트 리스너 추가
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        // 컴포넌트가 언마운트될 때 스크롤 이벤트 리스너 제거
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    const scrollToSection = (ref : any) => {
        ref.current.scrollIntoView({ behavior: 'smooth' });
    };
    return (
        <div className={cn('w-full fixed top-0 left-0',{'border-b' : scrolled})}>
            <div
                className='mx-auto my-0 bg-white h-[60px] flex items-center w-[calc(100%-80px)] max-w-[1280px]'>
                <p className='text-[20px] font-[700]'>기업해외 출장 보험</p>
                <div className='ml-auto'>
                    <ul className='flex space-x-8'>
                        <li className='li_hover' onClick={(event : React.MouseEvent) => scrollToSection(exampleRef)}>보험료 예시</li>
                        <li className='li_hover' onClick={(event : React.MouseEvent) => scrollToSection(infoRef)}>상품정보</li>
                        <li className='li_hover' >담보 및 보상한도</li>
                        <li className='li_hover' onClick={(event : React.MouseEvent) => scrollToSection(qaRef)}>Q & A</li>
                    </ul>
                </div>
            </div>
        </div>
    )
}
