import React, { useState, useEffect } from 'react';
import cn from 'classnames';

interface Props {
    exampleRef: React.RefObject<HTMLDivElement>;
    infoRef: React.RefObject<HTMLDivElement>;
    limitRef: React.RefObject<HTMLDivElement>;
    qaRef: React.RefObject<HTMLDivElement>;
}

export default function Header({ exampleRef, infoRef, limitRef, qaRef }: Props) {
    const [scrolled, setScrolled] = useState(false);
    const [activeSection, setActiveSection] = useState<string>('');

    const handleScroll = () => {
        const isScrolled = window.scrollY > 0;
        setScrolled(isScrolled);
        updateActiveSection();
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const updateActiveSection = () => {
        if (qaRef.current && window.scrollY >= qaRef.current.offsetTop) {
            setActiveSection('qa');
        } else if (limitRef.current && window.scrollY >= limitRef.current.offsetTop) {
            setActiveSection('limit');
        } else if (infoRef.current && window.scrollY >= infoRef.current.offsetTop) {
            setActiveSection('info');
        } else if (exampleRef.current && window.scrollY >= exampleRef.current.offsetTop-120) {
            setActiveSection('example');
        } else {
            setActiveSection('');
        }
    };

    const scrollToSection = (ref: React.RefObject<HTMLDivElement>) => {
        if (ref.current) {
            ref.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
    };

    const scrollToExample = () => {
        if (exampleRef.current) {
            scrollToSection(exampleRef);
        }
    };

    const scrollToInfo = () => {
        if (infoRef.current) {
            scrollToSection(infoRef);
        }
    };

    const scrollToQa = () => {
        if (qaRef.current) {
            scrollToSection(qaRef);
        }
    };

    return (
        <div className={cn('w-full fixed top-0 left-0', { 'border-b': scrolled })}>
            <div className='mx-auto my-0 bg-white h-[60px] flex items-center w-[calc(100%-80px)] max-w-[1280px]'>
                <p className='text-[20px] font-[700]'>기업해외 출장 보험</p>
                <div className='ml-auto'>
                    <ul className='flex space-x-8'>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'example' })} onClick={scrollToExample}>보험료 예시</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'info' })} onClick={scrollToInfo}>상품정보</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'limit' })} >담보 및 보상한도</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'qa' })} onClick={scrollToQa}>Q & A</li>
                    </ul>
                </div>
            </div>
        </div>
    );
}
