import React, { useState, useEffect } from 'react';
import cn from 'classnames';
import WhiteLogo from '../assets/images/PITIN LOGO_WHITE.png';
import PitInLogo from '../assets/images/pitin.png';

interface Props {
    registRef: React.RefObject<HTMLDivElement>;
    infoRef: React.RefObject<HTMLDivElement>;
    limitRef: React.RefObject<HTMLDivElement>;
    qaRef: React.RefObject<HTMLDivElement>;
}

export default function Header({ registRef, infoRef, limitRef, qaRef }: Props) {
    const [scrolled, setScrolled] = useState(false);
    const [activeSection, setActiveSection] = useState<string>('');

    const handleScroll = () => {
        const isScrolled = window.scrollY > 0;
        setScrolled(isScrolled);
        updateActiveSection();
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const updateActiveSection = () => {
        if (qaRef.current && window.scrollY >= qaRef.current.offsetTop) {
            setActiveSection('qa');
        } else if (infoRef.current && window.scrollY >= infoRef.current.offsetTop) {
            setActiveSection('info');
        } else if (limitRef.current && window.scrollY >= limitRef.current.offsetTop) {
            setActiveSection('limit');
        } else if (registRef.current && window.scrollY >= registRef.current.offsetTop) {
            setActiveSection('register');
        } else {
            setActiveSection('');
        }
    };

    const scrollToSection = (ref: React.RefObject<HTMLDivElement>) => {
        if (ref.current) {
            ref.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
    };

    const scrollToExample = () => {
        if (registRef.current) {
            scrollToSection(registRef);
        }
    };

    const scrollToInfo = () => {
        if (infoRef.current) {
            scrollToSection(infoRef);
        }
    };

    const scrollToLimit = () => {
        if (infoRef.current) {
            scrollToSection(limitRef);
        }
    };

    const scrollToQa = () => {
        if (qaRef.current) {
            scrollToSection(qaRef);
        }
    };

    return (
        <div className={cn('w-full fixed top-0 left-0 ', { 'scrollHeader': scrolled })}>
            <div className='mx-auto my-0 h-[100px] flex items-center w-[calc(100%-80px)] max-w-[1280px]'>
                {
                    scrolled ?
                    <img src = {WhiteLogo} alt = {'topLogo'} width={180}/>
                                    :
                    <img src={PitInLogo} alt={'topLogo'} width={180}/>
                }
                <div className='ml-auto'>
                    <ul className='flex space-x-8 cursor-pointer'>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'register' })} onClick={scrollToExample}>서비스 가입</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'limit' })} onClick={scrollToLimit}>요금안내 및 보상한도</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'info' })} onClick={scrollToInfo}>서비스 정보</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'qa' })} onClick={scrollToQa}>Q & A</li>
                    </ul>
                </div>
            </div>
        </div>
    );
}
