import React, { useState, useEffect } from 'react';
import cn from 'classnames';

interface Props {
    exampleRef: React.RefObject<HTMLDivElement>;
    infoRef: React.RefObject<HTMLDivElement>;
    limitRef: React.RefObject<HTMLDivElement>;
    qaRef: React.RefObject<HTMLDivElement>;
}

export default function Header({ exampleRef, infoRef, limitRef, qaRef }: Props) {
    const [scrolled, setScrolled] = useState(false);
    const [activeSection, setActiveSection] = useState('');

    const handleScroll = () => {
        const isScrolled = window.scrollY > 0;
        setScrolled(isScrolled);

        // 섹션 스크롤 위치를 기반으로 현재 활성화된 섹션을 설정
        const sectionTops = {
            example: exampleRef.current.offsetTop,
            info: infoRef.current.offsetTop,
            limit: limitRef.current.offsetTop,
            qa: qaRef.current.offsetTop,
        };

        const currentScroll = window.scrollY + window.innerHeight / 2;

        if (currentScroll < sectionTops.info) {
            setActiveSection('example');
        } else if (currentScroll < sectionTops.limit) {
            setActiveSection('info');
        } else if (currentScroll < sectionTops.qa) {
            setActiveSection('limit');
        } else {
            setActiveSection('qa');
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const scrollToSection = (ref: React.RefObject<HTMLDivElement>) => {
        ref.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
    };

    return (
        <div className={cn('w-full fixed top-0 left-0', { 'border-b': scrolled })}>
            <div className='mx-auto my-0 bg-white h-[60px] flex items-center w-[calc(100%-80px)] max-w-[1280px]'>
                <p className='text-[20px] font-[700]'>기업해외 출장 보험</p>
                <div className='ml-auto'>
                    <ul className='flex space-x-8'>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'example' })} onClick={() => scrollToSection(exampleRef)}>보험료 예시</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'info' })} onClick={() => scrollToSection(infoRef)}>상품정보</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'limit' })}>담보 및 보상한도</li>
                        <li className={cn('li_hover', { 'font-bold': activeSection === 'qa' })} onClick={() => scrollToSection(qaRef)}>Q & A</li>
                    </ul>
                </div>
            </div>
        </div>
    );
}
